var triggers = require('./..');

triggers.watch('notifications', function(err, notification) {

  if (err)
    return console.log(err);

  notification.on('opened', function(args){
    console.log('Opened: ' + args);
  })

  notification.on('closed', function(args){
    console.log('Closed: ' + args);
  })

  notification.on('clicked', function(args){
    console.log('Clicked: ' + args);
  })

})
