var triggers = require('./..');


triggers.watch('network', { respawn: true }, function(err, network) {
  if (err)
    throw err;

  console.log('Watching network triggers.');

  network.on('state_changed', function(args){
    console.log('Network state changed: ' + args);
  })

})

triggers.on('killed', function() {
  console.log('Child killed.');
})
